    private void campoMontoMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_campoMontoMouseClicked
        // Al hacer click en ingresar monto a depositar se borra el string por defecto
        if (campoMonto.getText().equals("Ingrese el monto a transferir")){
            campoMonto.setText("");
            campoMonto.setForeground(Color.black);
        }
    }//GEN-LAST:event_campoMontoMouseClicked

