    private void campoMontoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_campoMontoKeyTyped
		int key = evt.getKeyChar();

		boolean numeros = key >= 48 && key <= 57;

		if (!numeros) {
			evt.consume();
		}
    }//GEN-LAST:event_campoMontoKeyTyped

